def extract(input_data):
    import re
    spans = []
    text = input_data['text']
    values_pattern = r'\b\d{1,3}(?:[.,]\d{1,2})?\b'
    matches = re.finditer(values_pattern, text)
    for match in matches:
        if float(match.group(0).replace(',', '.')) > 0:
            spans.append({'text': match.group(0), 'start': match.start(), 'end': match.end()})
    return spans
