def extract(input_data):
    import re
    spans = []
    context = input_data['context']
    if not context.strip():
        return spans  # Early return for empty contexts
    pattern = re.compile(r'((?:[A-ZÄÖÜ][a-zäöüß]+(?: [a-zäöüß]+)*) (?:AG|Bank|Group|GmbH|Inc\.|LLC|PLC))')
    matches = pattern.finditer(context)
    for match in matches:
        if 'Bank' in match.group() or 'AG' in match.group():
            start, end = match.span()
            spans.append({'text': match.group(), 'start': start, 'end': end})
    return spans
